mapper.insert:
INSERT INTO SEMS.EDU_INFO (
							   EDU_ID 
							   , EDU_CTGR  
							   , EDU_TTL  
							   , MBR_ID 
							   , MAX_MBR 
							   , EDU_LOC 
							   , EDU_CLCM 
							   , EDU_INTR 
							   , SRT_DATE 
							   , END_DATE 
							   , START_TM 
							   , END_TM 
							   , EDU_TP 
							   , COST 
							) 
VALUES 					 (	#{educationId}
							 , #{educationCategory}
							 , #{educationTitle}
							 , #{memberId}
							 , #{maxMember}
							 , #{educationLocation}
							 , #{educationCurriculum}
							 , #{educationIntroduce}
							 , #{startDate}
							 , #{endDate }
							 , #{startTime}
							 , #{endTime}
							 , #{educationType}
							 , #{cost}
						)

mapper.update:
UPDATE	EDU_INFO
SET		EDU_ID = #{educationId}

		, EDU_CTGR = #{educationCategory}


		, EDU_TTL = #{educationTitle}


		, MBR_ID = #{memberId}


		, MAX_MBR = #{maxMember}


		, EDU_LOC = #{educationLocation}


		, EDU_CLCM = #{educationCurriculum}


		, EDU_INTR = #{educationIntroduce}


		, SRT_DATE = #{startDate}


		, END_DATE = #{endDate}


		, START_TM = #{startTime}


		, END_TM = #{endTime}


		, EDU_TP = #{educationType}


		, COST = #{cost}

WHERE	EDU_ID = #{educationId}

mapper.select:
SELECT JCE_HTR.EDU_HTR_ID educationHistoryId , JCE_HTR.EDU_TTL educationTitle , JCE_HTR.MBR_ID memberId , JCE_HTR.EDU_HTR_DT educationHistoryDate , JCE_HTR.STAT state , JCE_HTR.IP ip , JCE_HTR.CMNT cmnt , JCE_HTR.FDBK fdbk FROM ( SELECT ROWNUM AS RNUM , EDU_HTR_KEYWORD.* FROM ( SELECT EDU_HTR.*, EDU_INFO.EDU_TTL FROM EDU_HTR , EDU_INFO WHERE EDU_HTR.EDU_ID = EDU_INFO.EDU_ID
AND EDU_HTR.EDU_ID IN ( SELECT EDU_ID FROM EDU_INFO WHERE EDU_TTL IN #{searchKeyword} )
) EDU_HTR_KEYWORD WHERE ROWNUM <= #{endIndex} ) JCE_HTR WHERE RNUM >= #{startIndex}

SELECT JCE_HTR.EDU_HTR_ID educationHistoryId , JCE_HTR.EDU_TTL educationTitle , JCE_HTR.MBR_ID memberId , JCE_HTR.EDU_HTR_DT educationHistoryDate , JCE_HTR.STAT state , JCE_HTR.IP ip , JCE_HTR.CMNT cmnt , JCE_HTR.FDBK fdbk FROM ( SELECT ROWNUM AS RNUM , EDU_HTR_KEYWORD.* FROM ( SELECT EDU_HTR.*, EDU_INFO.EDU_TTL FROM EDU_HTR , EDU_INFO WHERE EDU_HTR.EDU_ID = EDU_INFO.EDU_ID AND STAT = 'EDU_JN_C'
AND EDU_HTR.EDU_ID IN ( SELECT EDU_ID FROM EDU_INFO WHERE EDU_TTL IN #{searchKeyword} )
) EDU_HTR_KEYWORD WHERE ROWNUM <= #{endIndex} ) JCE_HTR WHERE RNUM >= #{startIndex}

mapper.update:
UPDATE	 EDU_HTR
SET		 STAT = 'JOIN_CMPL'
WHERE	 EDU_ID LIKE #{educationId}
AND		 MBR_ID LIKE #{memberId}

UPDATE	 EDU_HTR
SET		 STAT = #{changeState}
WHERE	 EDU_HTR_ID = #{educationHistoryId}

mapper.select:
SELECT  STAT
FROM	EDU_HTR
WHERE	EDU_HTR_ID = #{educationHistoryId}
