mapper.select:
SELECT	COUNT(CD_ID)
FROM	GRDT_TP
WHERE	CD_NM = #{cdNm}

SELECT	COUNT(CD_ID)
FROM	GRDT_TP
WHERE	CD_ID = #{cdId}
OR		CD_NM = #{cdNm}

SELECT	COUNT(CD_ID)
FROM	HIGHEST_EDU_LV
WHERE	CD_ID = #{cdId}
OR		CD_NM = #{cdNm}

mapper.insert:
INSERT INTO MBR_TP (
                    CD_ID
                    , CD_NM
                    ) 
VALUES				( 
			        #{cdId}
			        , #{cdNm}
			        )

mapper.update:
UPDATE	MBR_TP
SET		CD_NM = #{cdNm}
WHERE	CD_ID = #{cdId}

UPDATE	GRDT_TP
SET		CD_NM = #{cdNm}
WHERE	CD_ID = #{cdId}

mapper.insert:
INSERT INTO GRDT_TP (
		 			  CD_ID
		 			  , CD_NM
		 			  ) 
VALUES 				(
						#{cdId}
						, #{cdNm}
					 )

mapper.update:
UPDATE	HIGHEST_EDU_LV
SET		CD_NM = #{cdNm}
WHERE	CD_ID = #{cdId}

mapper.insert:
INSERT INTO HIGHEST_EDU_LV (
		 			  CD_ID
		 			  , CD_NM
		 			  ) 
VALUES 				(
						#{cdId}
						, #{cdNm}
					 )
