mapper.insert:
INSERT INTO MBR	(
					ID
					, PWD
					, NM
					, EMAIL
					
					, HIGHEST_EDU_LV
					, UNIV_NM
					, MJR_NM
					, GRDT_TP
					
					, BRTH_DT
					, PHONE_NMB
					, MBR_TP
					, SALT
					, LATEST_LGI_DT
				)
VALUES 			( 
					#{id}
					, #{password}
					, #{name}
					, #{email}
					
					, #{highestEducationLevel}
					, #{universityName}
					, #{majorName}
					, #{graduationType}
					
					, #{birthDate}
					, #{phoneNumber}
					, #{memberType}
					, #{salt}
					, SYSDATE
				)

mapper.select:
SELECT	SALT salt
FROM	MBR
WHERE	ID = #{ id }

SELECT	PWD password
FROM	MBR
WHERE	ID = #{id}

SELECT	IS_ACC_LCK
FROM	MBR
WHERE	ID = #{id}

SELECT	ID
FROM	MBR
WHERE	ID = #{id}

mapper.insert:
INSERT INTO LGI_HTR	(
						LGI_HTR_ID
						, MBR_ID
						, LGI_IP
						, LGI_DT
					) 
VALUES				(
						#{ lgiHtrId }
						, #{ id }
						, #{ lgiIp }
						, SYSDATE
 					)

mapper.update:
UPDATE	LGI_HTR	
SET		LGO_DT = SYSDATE
WHERE	LGI_HTR_ID = #{lgiHtrId}

UPDATE	MBR
SET		MDF_F_CT = MDF_F_CT + 1
WHERE	ID = #{id}

mapper.select:
SELECT	COUNT(LGI_HTR_ID)
FROM	LGI_HTR
WHERE	MBR_ID = #{id}

mapper.insert:
		INSERT INTO ATD (
   							ID
   							, MBR_ID
   							, ATD_TM
   							, EDU_ID
   						) 
				VALUES 	( 
							ATD_ID_SEQ.NEXTVAL
							, #{memberId}
				 			, #{attendTime}
				 			, #{educationId}
				  		)

mapper.select:
SELECT	*
FROM	(
            SELECT  SUBSTR(ATD_TM, 1,10) ATD1
            FROM    ATD
            WHERE	MBR_ID = #{memberId}
            AND		EDU_ID = #{educationId}
            ORDER	BY ATD1 DESC
        )
WHERE  ROWNUM = 1

SELECT	CD_ID cdId
FROM	GRDT_TP
WHERE	CD_NM = #{cdNm}

SELECT	CD_ID cdId
FROM	HIGHEST_EDU_LV
WHERE	CD_NM = #{cdNm}

SELECT	ID
FROM	MBR
WHERE	EMAIL = #{email}
